// File: packages/core-engine/src/api/middleware/auth.ts
// Purpose: Token-based authentication middleware

import { Request, Response, NextFunction } from 'express';
import { config } from '../../utils/config';
import { logger } from '../../utils/logger';

export function authMiddleware(req: Request, res: Response, next: NextFunction) {
  const authHeader = req.headers.authorization;

  if (!authHeader || !authHeader.startsWith('Bearer ')) {
    logger.warn('Unauthorized request: missing or invalid token');
    return res.status(401).json({ 
      ok: false, 
      error: 'Unauthorized: missing or invalid authorization header' 
    });
  }

  const token = authHeader.substring(7); // Remove 'Bearer ' prefix

  if (token !== config.token) {
    logger.warn('Unauthorized request: token mismatch');
    return res.status(401).json({ 
      ok: false, 
      error: 'Unauthorized: invalid token' 
    });
  }

  logger.debug('Request authenticated successfully');
  next();
}
