import { useState, useEffect } from 'react';
import { Recipe } from '@aurora/shared';
import { apiClient } from '../lib/api';
import RecipeCard from '../components/RecipeCard';

export default function Recipes() {
  const [recipes, setRecipes] = useState<Recipe[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const ENGINE_URL = import.meta.env.VITE_ENGINE_URL || 'http://localhost:8080';
  const MOCK_MODE = import.meta.env.VITE_MOCK_MODE === 'true';
  const [engineAvailable, setEngineAvailable] = useState<boolean>(MOCK_MODE);

  useEffect(() => {
    loadRecipes();
    // Check engine availability when not in mock mode
    if (!MOCK_MODE) {
      (async () => {
        try {
          const resp = await fetch(ENGINE_URL, { method: 'GET' });
          setEngineAvailable(resp.ok);
        } catch (err) {
          setEngineAvailable(false);
        }
      })();
    }
  }, []);

  const loadRecipes = async () => {
    try {
      setLoading(true);
      const data = await apiClient.listRecipes();
      setRecipes(data);
      setError(null);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load recipes');
    } finally {
      setLoading(false);
    }
  };

  const handleRunRecipe = async (id: string) => {
    try {
      const result = await apiClient.runRecipe(id);
      if (result.ok) {
        alert('Recipe executed successfully!');
      } else {
        alert('Recipe execution failed');
      }
    } catch (err) {
      alert('Error running recipe: ' + (err instanceof Error ? err.message : 'Unknown error'));
    }
  };

  const handleDeleteRecipe = async (id: string) => {
    if (!confirm('Are you sure you want to delete this recipe?')) return;
    
    try {
      await apiClient.deleteRecipe(id);
      setRecipes(recipes.filter(r => r.id !== id));
    } catch (err) {
      alert('Error deleting recipe: ' + (err instanceof Error ? err.message : 'Unknown error'));
    }
  };

  if (loading) {
    return <div className="loading">Loading recipes...</div>;
  }

  return (
    <div className="page">
      <div className="page-header">
        <h1>Recipes</h1>
        <button onClick={() => setShowCreateForm(true)} className="btn-primary">
          Create Recipe
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="recipes-grid">
        {!engineAvailable && (
          <div className="warning-message">Engine not reachable. Run/Delete are disabled until a running engine is detected or enable mock mode.</div>
        )}
        {recipes.length === 0 ? (
          <p className="empty-state">No recipes yet. Create your first recipe!</p>
        ) : (
          recipes.map(recipe => (
            <RecipeCard
              key={recipe.id}
              recipe={recipe}
              onRun={handleRunRecipe}
              onDelete={handleDeleteRecipe}
              disabled={!MOCK_MODE && !engineAvailable}
            />
          ))
        )}
      </div>

      {showCreateForm && (
        <CreateRecipeModal
          onClose={() => setShowCreateForm(false)}
          onCreated={() => {
            setShowCreateForm(false);
            loadRecipes();
          }}
        />
      )}
    </div>
  );
}

function CreateRecipeModal({ onClose, onCreated }: { onClose: () => void; onCreated: () => void }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!name.trim()) return;

    try {
      setSubmitting(true);
      await apiClient.createRecipe({
        name: name.trim(),
        description: description.trim() || undefined,
        actions: [],
      });
      onCreated();
    } catch (err) {
      alert('Error creating recipe: ' + (err instanceof Error ? err.message : 'Unknown error'));
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal" onClick={e => e.stopPropagation()}>
        <h2>Create New Recipe</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Name *</label>
            <input
              type="text"
              value={name}
              onChange={e => setName(e.target.value)}
              placeholder="My Recipe"
              required
            />
          </div>
          <div className="form-group">
            <label>Description</label>
            <textarea
              value={description}
              onChange={e => setDescription(e.target.value)}
              placeholder="What does this recipe do?"
              rows={3}
            />
          </div>
          <div className="modal-actions">
            <button type="button" onClick={onClose} className="btn-secondary">
              Cancel
            </button>
            <button type="submit" disabled={submitting} className="btn-primary">
              {submitting ? 'Creating...' : 'Create'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
